                    <div
                      className={`w-12 h-12 ${getColorClasses(info.color).split(" ")[0]} ${getColorClasses(info.color).split(" ")[1]} rounded-xl flex items-center justify-center mr-4`}
                    >
                      <IconComponent className="w-5 h-5" />
